import "../styles/addFavourites.css"
import { useState } from "react";

let [title,setTitle]=useState("")
let [genre,setGenre]=useState('')
let [language,setLanguage]=useState('')
let [rating,setRating]=useState('')

let handleSubmit=(e)=>{
    e.preventDefault()
    let data=[title,genre,language,rating]
    console.log(data);
}

const AddFavourites = () => {
    return (
        <div className="addFavourites">
            <h1 style={{textAlign:'center'}}>Add to Favourites</h1>
            <div className="form">
                <div className="form_image">
                    <img width={350} height={500} src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-oeCmfp5uPuxmBmH4ZfPTIRPEvk8qqk8Y2g&usqp=CAU" alt="" />
                </div>
                <div className="form_inputs">
                    <form action="" onSubmit={handleSubmit}>
                        <div className="title">
                            <input type="text" placeholder="enter title" value={title} onChange={(e)=>setTitle(e.target.value)} />
                        </div>
                        <div className="genre">
                            <input type="text" placeholder="enter the genere of the movie" value={genre} onChange={(e)=>setGenre(e.target.value)} />
                        </div>
                        <div className="languge">
                            <input type="text" placeholder="enter languge" value={language} onChange={(e)=> setLanguage(e.target.value)}  />
                        </div>
                        <div className="rating">
                            <input type="text" placeholder="enter rating" value={rating} onChange={(e)=> setTitle(e.target.value)} />
                        </div>
                        <button>add to favourites</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default AddFavourites;
