const Movies = () => {
    let favourites = [
        {
            title: "The Boys",
            genre: "action",
            language: "English",
            rating: 4.5
        },
        {
            title: "Dark",
            genre: "sci-fi/mystery",
            language: "german",
            rating: 4.7
        },
        {
            title: "money heist",
            genre: "drama",
            language: "english",
            rating: 4.2
        },
        {
            title: "charlie",
            genre: "adventure/drama",
            language: "malayalam",
            rating: 5.0
        },
        {
            title: "Sita-Ramam",
            genre: "Romantic-Drama",
            language: "telugu",
            rating: 4.8
        }

    ]

    return (
        <div className="movies">

            <div className="favs">
                {
                    favourites.map((data) => (
                        <div className="fav_list">
                            <h2> Tittle: {data.title}</h2>
                            <p> genere/langauge: {data.genre}/{data.language}</p>
                            <p>  rating: {data.rating}</p>
                        </div>

                    ))
                }
            </div>

        </div>
    );
}

export default Movies;
