import '../styles/movies.css'
import { useState } from 'react'
import MoviesLogic from './moviesLogic'

const Movies = () => {

    // let favourites = [
    //     {
    //         title: "The Boys",
    //         genre: "action",
    //         language: "English",
    //         rating: 4.5,
    //         Id: 1
    //     },
    //     {
    //         title: "Dark",
    //         genre: "sci-fi/mystery",
    //         language: "german",
    //         rating: 4.7,
    //         Id: 2
    //     },
    //     {
    //         title: "money heist",
    //         genre: "drama",
    //         language: "english",
