    //         rating: 5.0,
    //         Id: 4
    //     },
    //     {
    //         title: "Sita-Ramam",
    //         genre: "Romantic-Drama",
    //         language: "telugu",
    //         rating: 4.8,
    //         Id: 5
    //     }

    // ]
    let [favouritesData, setFavsData] = useState([])

    let handlRemove = (Id, title) => {
        // alert(`${Id}  ${title}`)
        setFavsData(favouritesData.filter(x => x.Id != Id))
        alert(`${title} has been removed from favorites`)
    }
    // let topRating=(Id,title)=>{
    //     setFavsData(favouritesData.filter(x=>x.rating>4.5))

    // }
    return (
        <div className="movies">
            {/* <h1>Favourites of All Time</h1> */}

            <MoviesLogic title="favorites of all time"
                data={favouritesData}
                fn={handlRemove} />

            <MoviesLogic title="Top rating"
                data={favouritesData.filter(x => x.rating > 4.8)}
                fn={handlRemove} />
            <MoviesLogic title="Top English"
                data={favouritesData.filter(x => x.language=='English')}
                fn={handlRemove} />

        </div>
    );
}

export default Movies;
