import '../styles/movies.css'
import { useState } from 'react'

const Movies = () => {
    
    let favourites = [
        {
            title: "The Boys",
            genre: "action",
            language: "English",
            rating: 4.5,
            Id:1
        },
        {
            title: "Dark",
            genre: "sci-fi/mystery",
            language: "german",
            rating: 4.7,
            Id:2
        },
        {
            title: "money heist",
            genre: "drama",
            language: "english",
            rating: 4.2,
            Id:3
        },
        {
            title: "charlie",
            genre: "adventure/drama",
            language: "malayalam",
            rating: 5.0,
            Id:4
        },
        {
            title: "Sita-Ramam",
            genre: "Romantic-Drama",
            language: "telugu",
            rating: 4.8,
            Id:5
        }

    ]
    let [favouritesData,setFavsData]= useState(favourites)
    let handlRemove=(Id,title)=>{
        // alert(`${Id}  ${title}`)
        setFavsData(favouritesData.filter(x=>x.Id!=Id))
        alert(`${title} has been removed from favorites`)
      }

    return (
        <div className="movies">
                 <h1>Favourites of All Time</h1>
                 
            <div className="favs">
                {
                    favouritesData.map((data) => (
                        
                        <div className="favs_list">
                       
                            <h2> Tittle: {data.title}</h2>
                            <p> genere/langauge: {data.genre}/{data.language}</p>
                            <p>  rating: {data.rating}</p>
                            <button onClick={()=>handlRemove(data.Id,data.title)} >Remove</button>
                        </div>

                    ))
                }
            </div>

        </div>
    );
}

export default Movies;
