import '../styles/movies.css'


const Movies = () => {
   
    let handlRemove=(Id)=>{
        setValue(dta.Id)
    }
    let favourites = [
        {
            title: "The Boys",
            genre: "action",
            language: "English",
            rating: 4.5,
            Id:1
        },
        {
            title: "Dark",
            genre: "sci-fi/mystery",
            language: "german",
            rating: 4.7,
            Id:2
        },
        {
            title: "money heist",
            genre: "drama",
            language: "english",
            rating: 4.2,
            Id:3
        },
        {
            title: "charlie",
            genre: "adventure/drama",
            language: "malayalam",
            rating: 5.0,
            Id:4
        },
        {
            title: "Sita-Ramam",
            genre: "Romantic-Drama",
            language: "telugu",
            rating: 4.8,
            Id:5
        }

    ]

    return (
        <div className="movies">
                 <h1>Favourites of All Time</h1>
                 
            <div className="favs">
                {
                    favourites.map((data) => (
                        
                        <div className="favs_list">
                       
                            <h2> Tittle: {data.title}</h2>
                            <p> genere/langauge: {data.genre}/{data.language}</p>
                            <p>  rating: {data.rating}</p>
                            <button onClick={()=>handlRemove} >Remove</button>
                        </div>

                    ))
                }
            </div>

        </div>
    );
}

export default Movies;
